import { useState, useContext, useEffect } from 'react';
import Context from "../utils/Context";

function QuestionsPage() {
   const { triviaData, setTriviaData } = useContext(Context);
   const [questionIndex, setQuestionIndex] = useState(0);

   return (
      <div className="w3-content w3-container w3-margin-top">
         { questionIndex ?
            <div class="w3-card-4 w3-container w3-pale-blue">
               <h1>Question for { triviaData[questionIndex].player.name } </h1>
               <h2>{ triviaData[questionIndex].player.questionNo}</h2>
            </div> : <h1>Nothing Here</h1>
         }
      </div>
   )
}

export default QuestionsPage;
