import { useState, useContext } from 'react'
import { useNavigate } from "react-router-dom";
import axios from 'axios'
import Context from "../utils/Context"
import data from '../sampleData.json'

function CreateQuiz() {
   const { setTriviaData } = useContext(Context)
   const [formData, setFormData] = useState({ amount: '', category: '', difficulty: ''})
   const [player, setPlayer] = useState('')
   const [players, addPlayers] = useState([])
   const [error, setError] = useState('')
   const navigate = useNavigate();
   /*
   async function getTriviaData() {
      try {
         const { data } = await axios.get(`https://opentdb.com/api.php?amount=${amount}&category=${category}&difficulty=${difficulty}`)
         setTriviaData(data)
         setError('')
      } catch (err) {
         setError(err.response.data.message || err.message)
      }
   }*/

   function submitPlayer(event) {
      event.preventDefault()
      addPlayers(state => [...state, player])
      console.log(players)
      setPlayer('')
   }

   const handleInput = e => {
      // using destructuring to access e.target.name and e.target.value
      const { name, value } = e.target;
      // note the use of the [square brackets] here so we can use the name variable (instead of accessing a key of .name)
      setFormData({ ...formData, [name]: value }); 
   }

   const handlePlayer = e => {
      const { value } = e.target
      setPlayer(value)
   }

   function handleStart() {
      //getTriviaData()
      data = { ...data.results, amount }
      let i = 0
      let counter = 0
      while(i <= data.length - players.length) {
         counter++
         for(let k = 0; k < players.length; k++) {
            data[i+k].player = { name: players[k], questionNo: counter }
         }
         i += players.length
      }
      setTriviaData(data)
      navigate("/questions_page");
   }

   return (
      <div className="w3-content w3-container w3-margin-top">
         <div className="w3-card-4 w3-container w3-pale-blue">
            <h1>Create quiz - minimum 2 players</h1>
            <form onSubmit={ submitPlayer }>
               <input className='w3-margin-right' type="text" name="name" value={player} placeholder="Enter player name" onChange={handlePlayer} />
               <input className="w3-button w3-purple w3-round-xlarge" type="submit" value="Add" />
            </form>
            <form onSubmit={ submitForm }>
               <input className='w3-margin-right' type="text" name="amount" value={formData.amount} placeholder="Enter player name" onChange={handleInput} />
               <input className='w3-margin-right' type="text" name="category" value={formData.category} placeholder="Enter player name" onChange={handleInput} />
               <input className='w3-margin-right' type="text" name="difficulty" value={formData.difficulty} placeholder="Enter player name" onChange={handleInput} />
               <input className="w3-button w3-purple w3-round-xlarge" type="submit" value="Start" />
            </form>
            <h3>{error}</h3>
         </div>
         { players.length > 0 &&
               <>
                  <h3>Players</h3>
                  { players.map((e, i) => <li key={`player${i}`}>{e}</li>)}
               </>
            }
      </div>
   )
}

export default CreateQuiz;
