import { useContext, useState } from 'react'
import { useNavigate } from "react-router-dom";
import axios from 'axios'
import Context from "../utils/Context"
import data from '../sampleData.json'

function CreateQuiz() {
   const { triviaData, setTriviaData } = useContext(Context)
   const [ amount, setAmount] = useState('')
   const [category, setCategory] = useState('')
   const [difficulty, setDifficulty] = useState('')
   const [error, setError] = useState('')
   const navigate = useNavigate();

   async function getTriviaData() {
      try {
         const { data } = await axios.get(`https://opentdb.com/api.php?amount=${amount}&category=${category}&difficulty=${difficulty}`)
         setTriviaData(data)
         setError('')
      } catch (err) {
         setError(err.response.data.message || err.message)
      }
   }

   function submitForm(event) {
      event.preventDefault()
      getTriviaData()
      event.reset()
   }

   function handleChange(event) {
      const { value } = event.target
      setUsername(value)
   }

   function handleClick() {
      navigate("/questions_page");
   }

   return (
      <div className="w3-content w3-container w3-margin-top">
         { triviaData ?
         <div class="w3-card-4 w3-container w3-pale-blue">
            <h1>Create quiz - minimum 2 players</h1>
            <form onSubmit={ submitForm }>
               <input type="text" name="name" placeholder="Enter player name" onChange={handleChange} className='w3-margin-right'/>
               <input className="w3-button w3-purple w3-round-xlarge" type="submit" value="Submit" />
            </form>
            <h3>{error}</h3>
            <button onClick={handleClick} disabled>Start Quiz</button>
         </div> : <h1>Nothing Here</h1>
      }
      </div>
   )
}

export default CreateQuiz;
